import com.eviware.soapui.support.XmlHolder //xmlholder: To parse the xml 
import com.eviware.soapui.impl.wsdl.testcase.WsdlTestRunContext //to send requests

//Objective: Perform soap request through groovy
//1. get the request xml
//2. store in xmlholder
//3. set the node values
//4. save the xml


def additionReq = testRunner.testCase.testSuite.testCases["TC_Addition"].testSteps["Addition"].getPropertyValue("Request")
def xmlAdd = new XmlHolder(additionReq) //store the xml in xmlholder class
//log.info xmlAdd.getXml() //logs entire xml

//set node value
//xmlAdd.setNodeValue("xpath","value")
xmlAdd.setNodeValue("//tem:Add/tem:intA", "17")
log.info xmlAdd.getNodeValue("//tem:Add/tem:intA")

def no2 = testRunner.testCase.testSuite.testCases["TC_Addition"].getPropertyValue("n2")
log.info "no2: "+no2
xmlAdd.setNodeValue("//tem:Add/tem:intB", no2)
log.info xmlAdd.getNodeValue("//tem:Add/tem:intB")

//save the xml to new variable
def newAddXml = xmlAdd.getXml()
log.info newAddXml

//replace the TC_Addition request with above
testRunner.testCase.testSuite.testCases["TC_Addition"].testSteps["Addition"].setPropertyValue("Request", newAddXml)

//-------------------------------------------------------------------send request
//send request to server. Using wsdl run context
//context scope: test level
//changing the context scope to TC. Here 'TC_Addition' test case
//AddtestStep.run(testRunner, context)

def addTestStep = testRunner.testCase.testSuite.testCases["TC_Addition"].testSteps["Addition"]
//expects the step
def contextAdditionTC = new WsdlTestRunContext(addTestStep)
addTestStep.run(testRunner, contextAdditionTC)
//-------------------------------------------------------------------send request: end

def additionResponse = testRunner.testCase.testSuite.testCases["TC_Addition"].testSteps["Addition"].getPropertyValue("Response")

